import{_ as a,c as e,o as i,d as r}from"./app.99e6884a.js";var l="/imgs/blogs/amm1.png",t="/imgs/blogs/amm2.png",n="/imgs/blogs/amm3.png",s="/imgs/blogs/sushiswap1.png",p="/imgs/blogs/sushiswap2.png",o="/imgs/blogs/sushiswap3.png",h="/imgs/blogs/sushiswap4.png",d="/imgs/blogs/sushiswap5.png",u="/imgs/blogs/sushiswap6.png";const w='{"title":"AMM \u548C\u6D41\u52A8\u6C60\u7C7B\u578B","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u540D\u8BCD","slug":"\u540D\u8BCD"},{"level":2,"title":"\u4E2D\u5FC3\u5316\u4EA4\u6613\u6240\u7684\u505A\u5E02\u6A21\u5F0F","slug":"\u4E2D\u5FC3\u5316\u4EA4\u6613\u6240\u7684\u505A\u5E02\u6A21\u5F0F"},{"level":3,"title":"Order Book \u8BA2\u5355\u8584","slug":"order-book-\u8BA2\u5355\u8584"},{"level":3,"title":"AMM \u7684\u505A\u5E02\u6A21\u5F0F","slug":"amm-\u7684\u505A\u5E02\u6A21\u5F0F"},{"level":3,"title":"\u6D41\u52A8\u6C60\u7684\u5206\u7C7B","slug":"\u6D41\u52A8\u6C60\u7684\u5206\u7C7B"},{"level":2,"title":"\u6D41\u52A8\u6027\u6316\u77FF","slug":"\u6D41\u52A8\u6027\u6316\u77FF"},{"level":3,"title":"AMM \u7684\u51E0\u79CD\u6A21\u578B\u5206\u7C7B","slug":"amm-\u7684\u51E0\u79CD\u6A21\u578B\u5206\u7C7B"},{"level":3,"title":"AMM \u7684\u5C40\u9650\u6027","slug":"amm-\u7684\u5C40\u9650\u6027"},{"level":2,"title":"Sushiswap - not just a fork","slug":"sushiswap-not-just-a-fork"},{"level":3,"title":"\u5E73\u53F0\u529F\u80FD","slug":"\u5E73\u53F0\u529F\u80FD"},{"level":3,"title":"\u8D44\u91D1\u4F7F\u7528\u7387","slug":"\u8D44\u91D1\u4F7F\u7528\u7387"},{"level":3,"title":"Sushiswap Trident \u4E09\u53C9\u621F","slug":"sushiswap-trident-\u4E09\u53C9\u621F"},{"level":3,"title":"MISO - Minimal Initial SushiSwap Offering","slug":"miso-minimal-initial-sushiswap-offering"},{"level":3,"title":"Sh\u014Dyu - SushiSwap NFT Marketplace","slug":"shoyu-sushiswap-nft-marketplace"},{"level":3,"title":"Sushiswap \u672A\u6765","slug":"sushiswap-\u672A\u6765"}],"relativePath":"blogs/AMMandLiquidityPool.md","lastUpdated":1638883452273}',g={},m=r('<h1 id="amm-\u548C\u6D41\u52A8\u6C60\u7C7B\u578B" tabindex="-1">AMM \u548C\u6D41\u52A8\u6C60\u7C7B\u578B <a class="header-anchor" href="#amm-\u548C\u6D41\u52A8\u6C60\u7C7B\u578B" aria-hidden="true" target="_blank" rel="noopener">#</a></h1><h2 id="\u540D\u8BCD" tabindex="-1">\u540D\u8BCD <a class="header-anchor" href="#\u540D\u8BCD" aria-hidden="true" target="_blank" rel="noopener">#</a></h2><ul><li><p>AMM (Automated Market Maker) \u81EA\u52A8\u505A\u5E02\u5546</p></li><li><p>Liquidity \u6D41\u52A8\u6027, \u6307\u5404\u9879\u8D44\u4EA7\u7684\u6570\u91CF\u5206\u5E03\u60C5\u51B5</p></li><li><p>Liquidity Pool \u6D41\u52A8\u6027\u6C60, \u6307\u5404\u79CD\u8D44\u4EA7\u5BF9\u5E94\u7684\u8D44\u91D1\u6C60</p></li><li><p>LP (Liquidity Provider) \u6D41\u52A8\u6027\u63D0\u4F9B\u8005, \u5411\u6D41\u52A8\u6027\u6C60\u5185\u6DFB\u52A0\u5BF9\u5E94\u8D44\u4EA7\u7684\u7528\u6237</p></li><li><p>Order Book \u8BA2\u5355\u8584</p></li><li><p>Slippage \u6ED1\u70B9, \u6307\u4E0B\u5355\u4EF7\u683C\u548C\u6700\u540E\u6210\u4EA4\u4EF7\u7684\u5DEE\u989D\u6BD4\u4F8B</p></li></ul><h2 id="\u4E2D\u5FC3\u5316\u4EA4\u6613\u6240\u7684\u505A\u5E02\u6A21\u5F0F" tabindex="-1">\u4E2D\u5FC3\u5316\u4EA4\u6613\u6240\u7684\u505A\u5E02\u6A21\u5F0F <a class="header-anchor" href="#\u4E2D\u5FC3\u5316\u4EA4\u6613\u6240\u7684\u505A\u5E02\u6A21\u5F0F" aria-hidden="true" target="_blank" rel="noopener">#</a></h2><h3 id="order-book-\u8BA2\u5355\u8584" tabindex="-1">Order Book \u8BA2\u5355\u8584 <a class="header-anchor" href="#order-book-\u8BA2\u5355\u8584" aria-hidden="true" target="_blank" rel="noopener">#</a></h3><p>\u8BB0\u5F55\u7740\u4E70\u5356\u53CC\u65B9\u5BF9\u8D44\u4EA7\u7684\u62A5\u4EF7\u60C5\u51B5</p><ul><li>\u6302\u5355\u8005 Maker, \u4E5F\u53EB\u505A\u5E02\u5546</li></ul><p>\u4E13\u4E1A\u7684\u505A\u5E02\u5546\u4F1A\u540C\u65F6\u5728\u4EA4\u6613\u6570\u7684\u4E70\u5356\u4E24\u4E2A\u65B9\u5411\u6302\u5355,\u5E76\u4E14\u6839\u636E\u5E02\u573A\u7684\u6CE2\u52A8\u8C03\u6574\u62A5\u4EF7, \u4E3A\u4EA4\u6613\u6240\u63D0\u4F9B\u6D41\u52A8\u6027,\u5438\u5F15\u66F4\u591A\u7684\u4EA4\u6613\u8005\u6765\u8FDB\u884C\u4EA4\u6613</p><p>\u7B80\u5355\u8BF4\u505A\u5E02\u5546\u5C31\u662F\u63D0\u4F9B\u62A5\u4EF7\u4E0E\u6D41\u52A8\u6027\u7684\u4EBA</p><p>\u5728\u505A\u5E02\u884C\u4E3A\u4E2D,\u6302\u5355\u7684\u8D44\u4EA7\u6570\u91CF\u79F0\u4E3A\u6D41\u52A8\u6027,\u63D0\u4F9B\u8D44\u4EA7\u7684\u4EBA\u79F0\u4E3A\u6D41\u52A8\u6027\u63D0\u4F9B\u8005(LP). LP \u63D0\u4F9B\u6D41\u52A8\u6027\u4E4B\u540E, \u8FD8\u9700\u8981\u4E3A\u8D44\u4EA7\u63D0\u4F9B\u4E00\u4E2A\u4EF7\u683C, \u8FD9\u6837\u624D\u7B97\u662F\u5B8C\u6210\u505A\u5E02.</p><p><img src="'+l+'" alt="Order Book"></p><p>\u6CA1\u6709\u505A\u5E02\u5546\u6216\u505A\u5E02\u5546\u5F88\u5C11,\u4EA4\u6613\u8005\u8FDB\u884C\u4EA4\u6613\u65F6\u5C31\u4F1A\u9762\u4E34\u6CA1\u6709\u6D41\u52A8\u6027\u7684\u60C5\u51B5,\u7F3A\u4E4F\u6D41\u52A8\u6027\u4E5F\u5C31\u610F\u5473\u7740\u4F1A\u9020\u6210\u9AD8\u6ED1\u70B9\u4EA4\u6613</p><ul><li>\u5403\u5355\u8005 Taker, \u4E5F\u53EB\u4EA4\u6613\u8005</li></ul><h3 id="amm-\u7684\u505A\u5E02\u6A21\u5F0F" tabindex="-1">AMM \u7684\u505A\u5E02\u6A21\u5F0F <a class="header-anchor" href="#amm-\u7684\u505A\u5E02\u6A21\u5F0F" aria-hidden="true" target="_blank" rel="noopener">#</a></h3><ul><li><p>\u7531 LP \u6765\u63D0\u4F9B\u6D41\u52A8\u6027, LP \u5C06\u81EA\u6709\u8D44\u4EA7\u6309\u6BD4\u4F8B\u5B58\u5165\u6C60\u5B50\u4E2D\u4E3A\u4EA4\u6613\u63D0\u4F9B\u6D41\u52A8\u6027, \u8FD9\u4E2A\u6C60\u5B50\u5C31\u662F\u6D41\u52A8\u6027\u6C60</p></li><li><p>\u5F53\u4EA4\u6613\u8005\u5728\u8FDB\u884C\u4EA4\u6613\u65F6, \u5B9E\u9645\u4E0A\u662F\u4E0E\u6D41\u52A8\u6027\u6C60\u8FDB\u884C\u4EA4\u6613, \u800C\u4EA4\u6613\u7684\u4EF7\u683C\u5219\u7531 AMM \u6A21\u578B\u6839\u636E\u53C2\u6570\u81EA\u52A8\u751F\u6210</p></li></ul><h3 id="\u6D41\u52A8\u6C60\u7684\u5206\u7C7B" tabindex="-1">\u6D41\u52A8\u6C60\u7684\u5206\u7C7B <a class="header-anchor" href="#\u6D41\u52A8\u6C60\u7684\u5206\u7C7B" aria-hidden="true" target="_blank" rel="noopener">#</a></h3><ul><li><p>\u6052\u5B9A\u8D44\u4EA7\u6BD4\u4F8B\u6C60: \u4E24\u79CD\u8D44\u4EA7\u7684\u6BD4\u4F8B 50/50</p></li><li><p>\u6DF7\u5408\u8D44\u4EA7\u6C60: \u652F\u6301 3 \u79CD\u53CA\u4EE5\u4E0A\u8D44\u4EA7, \u4F8B\u5982 Curve</p></li></ul><p>\u8D44\u4EA7\u4EA4\u6613\u66F4\u52A0\u7B80\u5355, \u6210\u672C\u66F4\u4F4E</p><ul><li>\u52A0\u6743\u6C60: \u591A\u79CD\u8D44\u4EA7\u6BD4\u4F8B, \u4F8B\u5982 Balancer</li></ul><p>\u8D44\u4EA7\u6309\u6BD4\u4F8B\u5B58\u5165\u52A0\u6743\u6C60,\u5F53\u5916\u90E8\u4EF7\u683C\u53D1\u751F\u53D8\u5316, \u5BFC\u81F4\u8D44\u4EA7\u6BD4\u4F8B\u53D1\u751F\u53D8\u5316, \u5957\u5229\u8005\u901A\u8FC7\u4EA4\u6613\u4F1A\u81EA\u52A8\u91CD\u65B0\u5E73\u8861\u8D44\u91D1\u6C60. \u7528\u6237\u5728\u52A0\u6743\u6C60\u63D0\u4F9B\u6D41\u52A8\u6027\u7684\u540C\u65F6, \u53EF\u4EE5\u4FDD\u6301\u8D44\u4EA7\u6BD4\u4F8B\u4E0D\u53D8, \u800C\u4E14\u8FD8\u53EF\u4EE5\u83B7\u5F97\u4EA4\u6613\u624B\u7EED\u8D39</p><ul><li><p>LP \u6839\u636E\u63D0\u4F9B\u7684\u8D44\u91D1\u5360\u6C60\u5B50\u7684\u6BD4\u4F8B,\u5C06\u83B7\u5F97\u76F8\u5E94\u7684 LP Token, \u76F8\u5F53\u4E8E\u4E00\u79CD\u5B58\u6B3E\u51ED\u8BC1.</p></li><li><p>\u7528\u6237\u6839\u636E\u81EA\u5DF1\u7684\u8D44\u91D1\u6BD4\u4F8B\u6240\u5206\u5F97\u7684\u4EA4\u6613\u624B\u7EED\u8D39\u4F1A\u7D2F\u79EF\u5230 LP Token \u7684\u4EF7\u503C\u4E2D</p></li></ul><h2 id="\u6D41\u52A8\u6027\u6316\u77FF" tabindex="-1">\u6D41\u52A8\u6027\u6316\u77FF <a class="header-anchor" href="#\u6D41\u52A8\u6027\u6316\u77FF" aria-hidden="true" target="_blank" rel="noopener">#</a></h2><p>\u7528\u6237\u63D0\u4F9B\u6D41\u52A8\u6027\u540E\u4ECE\u4E2D\u83B7\u5F97\u6536\u76CA\u7684\u8FD9\u4E00\u884C\u4E3A\u88AB\u79F0\u4E3A<strong>\u6D41\u52A8\u6027\u6316\u77FF</strong></p><h3 id="amm-\u7684\u51E0\u79CD\u6A21\u578B\u5206\u7C7B" tabindex="-1">AMM \u7684\u51E0\u79CD\u6A21\u578B\u5206\u7C7B <a class="header-anchor" href="#amm-\u7684\u51E0\u79CD\u6A21\u578B\u5206\u7C7B" aria-hidden="true" target="_blank" rel="noopener">#</a></h3><ul><li>\u6052\u5B9A\u4E58\u79EF\u505A\u5E02\u5546(CPMM)</li></ul><p>Uniswap \u5C31\u662F CPMM+\u6052\u5B9A\u8D44\u91D1\u6BD4\u4F8B\u6C60(50/50)</p><p>\u51FD\u6570 x*y=k, x \u548C y \u5206\u522B\u4EE3\u8868\u6D41\u52A8\u6C60\u4E2D\u7684\u4E24\u79CD\u8D44\u4EA7\u7684\u6570\u91CF, k \u662F\u6052\u5B9A\u7684\u5E38\u6570</p><ul><li>\u6052\u5B9A\u548C\u505A\u5E02\u5546(CSMM)</li></ul><p>\u516C\u5F0F x+y=k, \u6781\u7AEF\u60C5\u51B5\u4E0B\u5BB9\u6613\u8017\u5C3D\u6C60\u5185\u4E00\u79CD\u8D44\u4EA7, \u6240\u4EE5\u5E76\u6CA1\u6709\u88AB\u91C7\u7528</p><ul><li>\u6052\u5B9A\u5E73\u5747\u503C\u505A\u5E02\u5546(CMMM)</li></ul><p>\u652F\u6301 2 \u79CD\u4EE5\u4E0A\u8D44\u4EA7,\u5E76\u53EF\u4EE5\u8FDB\u884C\u52A0\u6743. \u6D41\u52A8\u6C60\u91C7\u7528\u6DF7\u5408\u8D44\u91D1\u6C60\u6216\u52A0\u6743\u6C60.</p><p>\u516C\u5F0F: (x<em>y</em> z)^(1/3)=k</p><p>\u5141\u8BB8\u6C60\u5185\u4E0D\u540C\u8D44\u4EA7\u7684\u98CE\u9669\u655E\u53E3\u53EF\u53D8,\u5E76\u53EF\u5728\u6C60\u5185\u4EFB\u4F55\u8D44\u4EA7\u95F4\u8FDB\u884C\u4E92\u6362</p><ul><li>\u6DF7\u5408\u5E38\u6570\u51FD\u6570\u505A\u5E02\u5546(CFMM)</li></ul><p>Curve \u7684 CFMM \u5C31\u7ED3\u5408\u4E86 CPMM \u548C CSMM,\u4EE5\u521B\u9020\u66F4\u597D\u7684\u6D41\u52A8\u6027,\u5728\u7ED9\u5B9A\u7684\u4EA4\u6613\u8303\u56F4\u5185\u964D\u4F4E\u6ED1\u70B9. \u5BF9\u5927\u591A\u6570\u4EA4\u6613\u96C6\u4E2D\u5728\u7EBF\u6027\u6C47\u7387, \u800C\u5BF9\u5927\u989D\u7684\u4EA4\u6613\u503C\u8FD4\u56DE\u6307\u6570\u4EF7\u683C</p><h3 id="amm-\u7684\u5C40\u9650\u6027" tabindex="-1">AMM \u7684\u5C40\u9650\u6027 <a class="header-anchor" href="#amm-\u7684\u5C40\u9650\u6027" aria-hidden="true" target="_blank" rel="noopener">#</a></h3><ul><li>\u65E0\u5E38\u635F\u5931(Impermanent Loss)\u7684\u98CE\u9669</li></ul><p>\u5F53\u573A\u5916\u7684\u4EF7\u683C\u548C\u6D41\u52A8\u6C60\u5185\u5BF9\u5E94\u7684\u4EE3\u5E01\u4EF7\u683C\u5F80\u4EFB\u4F55\u65B9\u5411\u504F\u79BB\u65F6,\u5C31\u4F1A\u4EA7\u751F\u65E0\u5E38\u635F\u5931</p><p>AMM \u4E0D\u4F1A\u611F\u5E94\u5E02\u573A\u4EF7\u683C -&gt; \u4EF7\u683C\u53D8\u52A8 -&gt; \u5957\u5229\u8005\u5E73\u8861 -&gt; \u4ECE LP \u7684\u8D44\u91D1\u62BD\u53D6\u5229\u6DA6 -&gt; LP \u7684\u65E0\u5E38\u635F\u5931</p><p><a href="https://decentyields.com/impermanent-loss-calculator" target="_blank" rel="noopener noreferrer">Impermanent Loss Calculator</a></p><ul><li>\u8D44\u672C\u6548\u7387\u4F4E</li></ul><p><img src="'+t+'" alt="\u4E3B\u52A8\u505A\u5E02"></p><p>\u5728 Uniswap v3 \u4E2D,\u4F7F\u7528\u4E86\u65B0\u7684 AMM \u6A21\u578B,\u5F15\u51FA\u4E86<strong>\u4E3B\u52A8\u505A\u5E02</strong> \u7684\u6D41\u52A8\u6027\u5206\u914D\u65B9\u5F0F, \u53EF\u4EE5\u8BA9\u7528\u6237\u81EA\u4E3B\u9009\u62E9\u6D41\u52A8\u6027\u7684\u5206\u5E03\u8303\u56F4, \u901A\u8FC7\u96C6\u4E2D\u6D41\u52A8\u6027, \u7528\u6237\u4F1A\u5C06\u6D41\u52A8\u6027\u4E3B\u52A8\u5206\u5E03\u5728\u8D44\u91D1\u5229\u7528\u7387\u6700\u9AD8\u7684\u4EF7\u683C\u8303\u56F4, \u5373\u5F53\u524D\u4EF7\u683C\u8303\u56F4\u9644\u8FD1</p><p><img src="'+n+'" alt="\u52A8\u6001\u505A\u5E02"></p><p>\u4EE5 DODO \u53CA Curve \u4E3A\u4EE3\u8868\u7684 DEX, \u91C7\u7528\u4E86\u52A8\u6001\u505A\u5E02\u7684\u6A21\u578B, \u52A8\u6001\u505A\u5E02\u7684\u6838\u5FC3\u5C31\u662F\u4F1A\u6839\u636E\u9884\u8A00\u673A\u7684\u62A5\u4EF7, \u52A8\u6001\u8C03\u6574\u6D41\u52A8\u6027\u5206\u5E03, \u5C06\u6D41\u52A8\u6027\u59CB\u7EC8\u805A\u96C6\u5728\u5F53\u524D\u4EF7\u683C\u9644\u8FD1,\u4E0D\u9700\u8981\u7528\u6237\u624B\u52A8\u8FDB\u884C\u64CD\u4F5C.</p><ul><li>\u591A\u4EE3\u5E01\u655E\u53E3\u7684\u98CE\u9669</li></ul><p>\u7528\u6237\u8981\u627F\u53D7\u591A\u79CD\u4EE3\u5E01\u4EF7\u683C\u6D6E\u52A8\u7684\u98CE\u9669</p><p>Curve \u53CA Bancor \u7B49\u5E73\u53F0\u5C31\u5141\u8BB8 LP \u5728\u63D0\u4F9B\u6D41\u52A8\u6027\u65F6,\u53EF\u4EE5\u9009\u62E9\u53EA\u5B58\u5165\u4E00\u79CD\u4EE3\u5E01, \u53D6\u51FA\u6D41\u52A8\u8D44\u91D1\u65F6\u4E5F\u53EF\u4EE5\u5236\u5B9A\u67D0\u4E00\u79CD\u4EE3\u5E01\u63D0\u53D6</p><h2 id="sushiswap-not-just-a-fork" tabindex="-1">Sushiswap - not just a fork <a class="header-anchor" href="#sushiswap-not-just-a-fork" aria-hidden="true" target="_blank" rel="noopener">#</a></h2><h3 id="\u5E73\u53F0\u529F\u80FD" tabindex="-1">\u5E73\u53F0\u529F\u80FD <a class="header-anchor" href="#\u5E73\u53F0\u529F\u80FD" aria-hidden="true" target="_blank" rel="noopener">#</a></h3><p><img src="'+s+'" alt="sushiswap\u5E73\u53F0"></p><h4 id="multi-chain-amm-\u591A\u94FE-amm" tabindex="-1">Multi-chain AMM \u591A\u94FE AMM <a class="header-anchor" href="#multi-chain-amm-\u591A\u94FE-amm" aria-hidden="true" target="_blank" rel="noopener">#</a></h4><p>\u6210\u7ACB<a href="https://forum.sushi.com/t/multichain-expansion-operational-fund/4549" target="_blank" rel="noopener noreferrer">Multichain Expansion Fund</a>\u591A\u94FE\u53D1\u5C55\u57FA\u91D1</p><h4 id="kashi-lending-leverage" tabindex="-1">Kashi Lending &amp; Leverage <a class="header-anchor" href="#kashi-lending-leverage" aria-hidden="true" target="_blank" rel="noopener">#</a></h4><ul><li>Lend \u501F\u51FA\u60A8\u7684\u8D44\u4EA7\uFF0C\u8D5A\u53D6\u6536\u76CA\u65E0\u65E0\u5E38\u635F\u5931</li></ul><p>\u5B64\u7ACB\u501F\u8D37\u5E02\u573A\u964D\u4F4E\u60A8\u4F5C\u4E3A\u8D44\u4EA7\u63D0\u4F9B\u4EBA\u7684\u98CE\u9669\u3002\u5728\u5BF9\u624B\u65B9\u7834\u4EA7\u65F6\u786E\u5207\u77E5\u9053\u5BF9\u65B9\u7684\u62B5\u62BC\u54C1\u3002</p><ul><li>Borrow \u501F\u5165\u8D44\u4EA7\u5E76\u63D0\u9AD8\u6760\u6746\u7387</li></ul><p>\u501F\u6B3E\u8BA9\u60A8\u5728\u4E0D\u51FA\u552E\u8D44\u4EA7\u7684\u60C5\u51B5\u4E0B\u83B7\u5F97\u6D41\u52A8\u6027\u3002\u60A8\u7684\u501F\u6B3E\u4E0A\u9650\u53D6\u51B3\u4E8E\u60A8\u62B5\u62BC\u7269\u7684\u4EF7\u503C\u3002\u60A8\u53EF\u4EE5\u501F\u51FA\u60A8\u62B5\u62BC\u54C1\u4EF7\u503C\u7684 75%\uFF0C\u968F\u65F6\u53EF\u4EE5\u8FD8\u6B3E\uFF0C\u5E76\u9644\u4E0A\u5229\u606F\u3002</p><ul><li>Create \u521B\u5EFA\u4E00\u4E2A\u65B0\u7684 Kashi \u5E02\u573A</li></ul><p>\u5982\u679C\u60A8\u60F3\u8981\u5411\u5C1A\u672A\u4E0A\u67B6\u7684\u5E02\u573A\u4F9B\u5E94\uFF0C\u60A8\u53EF\u4EE5\u4F7F\u7528\u8FD9\u4E2A\u5DE5\u5177\u6765\u521B\u5EFA\u4E00\u4E2A\u65B0\u7684\u4EA4\u6613\u5BF9\u3002</p><h4 id="bentobox-dapps" tabindex="-1">BentoBox dApps <a class="header-anchor" href="#bentobox-dapps" aria-hidden="true" target="_blank" rel="noopener">#</a></h4><p>BentoBox \u53EF\u4EE5\u5B9E\u73B0\u7528\u5F88\u4F4E\u7684 gas \u8D39\u7528\u4F53\u9A8C\u9AD8\u6536\u76CA\u7684 dapp\u3002</p><ul><li>BentoBox \u5C06\u4EE3\u5E01\u5B58\u5165 BentoBox\uFF0C\u4EE5\u83B7\u5F97\u6240\u6709\u6536\u76CA\u3002</li></ul><p>BentoBox \u901A\u8FC7\u5728\u96C6\u6210\u7684 dapp \u4E2D\u4F7F\u7528\u95EA\u7535\u8D37\u3001\u7B56\u7565\u4EE5\u53CA\u56FA\u5B9A\u548C\u4F4E Gas \u7684\u8F6C\u8D26\uFF0C\u6765\u63D0\u4F9B\u989D\u5916\u6536\u76CA</p><h4 id="xsushi-staking-sushibar" tabindex="-1">xSUSHI staking - SushiBar <a class="header-anchor" href="#xsushi-staking-sushibar" aria-hidden="true" target="_blank" rel="noopener">#</a></h4><p>\u5C06 SUSHI \u8D28\u62BC\u6210 xSUSHI \u6700\u5927\u5316\u6536\u76CA</p><p>\u6BCF\u6761\u94FE\u4E0A\u6BCF\u7B14\u4EA4\u6613\u624B\u7EED\u8D39\u7684 0.05% \u90FD\u6309\u60A8\u7684 SushiBar \u4EFD\u989D\u6BD4\u4F8B\u5206\u914D\u4E3A SUSHI\u3002\u5F53\u60A8\u7684 SUSHI \u8D28\u62BC\u5728 SushiBar \u7684\u65F6\u5019\uFF0C\u60A8\u5C06\u4F1A\u6536\u5230 xSUSHI\uFF0C\u5B83\u5177\u6709\u6CBB\u7406\u6743\u4E14\u53EF\u4EE5\u4E0E\u5176\u5B83\u534F\u8BAE\u4EA4\u4E92\u3002xSUSHI \u53EF\u6301\u7EED\u590D\u5229\uFF0C\u5F53\u60A8\u89E3\u9664\u8D28\u62BC\u65F6\uFF0C\u60A8\u5C06\u6536\u5230\u8D28\u62BC\u65F6\u7684 SUSHI \u53CA\u624B\u7EED\u8D39\u3002</p><p><img src="'+p+'" alt="SushiBar"></p><h4 id="onsen-program" tabindex="-1">Onsen Program <a class="header-anchor" href="#onsen-program" aria-hidden="true" target="_blank" rel="noopener">#</a></h4><h3 id="\u8D44\u91D1\u4F7F\u7528\u7387" tabindex="-1">\u8D44\u91D1\u4F7F\u7528\u7387 <a class="header-anchor" href="#\u8D44\u91D1\u4F7F\u7528\u7387" aria-hidden="true" target="_blank" rel="noopener">#</a></h3><table><thead><tr><th><strong>DEX \u5E73\u53F0</strong></th><th><strong>\u8D44\u91D1\u4F7F\u7528\u7387</strong></th></tr></thead><tbody><tr><td>Uniswap v2</td><td>5.8%</td></tr><tr><td>Curve</td><td>1.3%</td></tr><tr><td>Sushiswap</td><td>6.6%</td></tr><tr><td>Uniswap v3</td><td>22.7%</td></tr></tbody></table><h3 id="sushiswap-trident-\u4E09\u53C9\u621F" tabindex="-1">Sushiswap Trident \u4E09\u53C9\u621F <a class="header-anchor" href="#sushiswap-trident-\u4E09\u53C9\u621F" aria-hidden="true" target="_blank" rel="noopener">#</a></h3><p>\u4EE5<strong>\u8D44\u672C\u6548\u7387</strong> \u548C\u52A0\u5BC6\u8D27\u5E01<strong>\u6CE2\u52A8\u6027\u4FDD\u62A4</strong> \u4E3A\u6838\u5FC3\u91CD\u70B9\u5F00\u53D1\u7684, \u4F7F\u7528\u5F3A\u5927\u800C\u76F4\u89C2\u7684\u4FBF\u6377\u5DE5\u5177, \u4E3A DeFi \u63D0\u4F9B\u5176\u65B0\u7684\u534F\u8BAE\u6807\u51C6</p><h4 id="\u589E\u52A0\u4E86\u4E0D\u540C\u7C7B\u578B\u7684\u6D41\u52A8\u6027\u6C60" tabindex="-1">\u589E\u52A0\u4E86\u4E0D\u540C\u7C7B\u578B\u7684\u6D41\u52A8\u6027\u6C60 <a class="header-anchor" href="#\u589E\u52A0\u4E86\u4E0D\u540C\u7C7B\u578B\u7684\u6D41\u52A8\u6027\u6C60" aria-hidden="true" target="_blank" rel="noopener">#</a></h4><p>\u6539\u8FDB\u6052\u5B9A\u8D44\u4EA7\u6D41\u52A8\u6027\u6C60</p><ul><li>Zap in/zap out \u529F\u80FD, \u53EF\u4EE5\u6DFB\u52A0\u4EFB\u4F55\u8D44\u4EA7\u6570\u91CF, Sushiswap \u5C06\u5728\u540E\u53F0\u5151\u6362\u6210\u4EF7\u503C\u76F8\u7B49\u7684\u8D44\u4EA7\u5E76\u589E\u52A0\u6D41\u52A8\u6027. \u7C7B\u4F3C Curve \u6216 Balancer \u652F\u6301\u7684\u81EA\u9009\u4EE3\u5E01\u5B58\u53D6\u9009\u9879</li></ul><p>\u589E\u52A0</p><ol><li>\u96C6\u4E2D\u6D41\u52A8\u6027\u6C60(Concentrated)</li></ol><p>\u7C7B\u4F3C Uniswap v3 \u7684\u4E3B\u52A8\u505A\u5E02\u6A21\u5F0F, \u4F7F\u7528\u6237\u81EA\u5B9A\u4E49\u505A\u5E02\u533A\u95F4,\u8FDB\u4E00\u6B65\u63D0\u5347\u8D44\u672C\u6548\u7387</p><p>\u5F53\u5728\u8BE5\u7C7B\u578B\u7684\u6D41\u52A8\u6027\u6C60\u5185\u505A LP \u65F6, \u7528\u6237\u5C06\u6536\u5230\u4E00\u4E2A**ERC-721(NFT)**\u5F62\u5F0F\u7684 LP \u5B58\u6B3E\u51ED\u8BC1</p><ol><li>\u7A33\u5B9A\u6C60(Stable)</li></ol><ul><li><p>On-peg tokens</p></li><li><p>\u8D44\u4EA7\u6BD4\u4F8B\u5E73\u5747\u5206\u914D</p></li><li><p>\u4F4E\u6ED1\u70B9\u7684\u7A33\u5B9A\u4EA4\u6613\u66F2\u7EBF</p></li><li><p>\u672A\u6765\u5141\u8BB8 2~32 \u79CD\u8D44\u4EA7</p></li></ul><ol><li>\u6307\u6570\u8D44\u4EA7\u6C60(Index)</li></ol><ul><li><p>\u6309\u7167\u6C60\u5B50\u7684\u521B\u5EFA\u8005\u8BBE\u5B9A\u7684\u89C4\u683C\u52A0\u6743</p></li><li><p>\u540C\u65F6\u652F\u6301 2 \u5230 8 \u4E2A\u4EE3\u5E01</p></li><li><p>\u8D44\u4EA7\u6BD4\u4F8B\u4E4B\u548C\u4E3A 100%</p></li><li><p>\u6839\u636E\u6743\u91CD\u5728\u4EE3\u5E01\u4E4B\u95F4\u5206\u914D\u4EF7\u683C\u5F71\u54CD</p></li></ul><h4 id="tines-\u8DEF\u7531\u5F15\u64CE" tabindex="-1">Tines(\u8DEF\u7531\u5F15\u64CE) <a class="header-anchor" href="#tines-\u8DEF\u7531\u5F15\u64CE" aria-hidden="true" target="_blank" rel="noopener">#</a></h4><p><img src="'+o+'" alt="SushiBar"></p><h4 id="\u5BF9-bentobox-\u8FDB\u884C\u5347\u7EA7" tabindex="-1">\u5BF9 Bentobox \u8FDB\u884C\u5347\u7EA7 <a class="header-anchor" href="#\u5BF9-bentobox-\u8FDB\u884C\u5347\u7EA7" aria-hidden="true" target="_blank" rel="noopener">#</a></h4><p><img src="'+h+'" alt="Bentobox1"></p><p><img src="'+d+'" alt="Bentobox2"></p><h3 id="miso-minimal-initial-sushiswap-offering" tabindex="-1">MISO - Minimal Initial SushiSwap Offering <a class="header-anchor" href="#miso-minimal-initial-sushiswap-offering" aria-hidden="true" target="_blank" rel="noopener">#</a></h3><p>IDO \u4E00\u7AD9\u5F0F\u5E73\u53F0,\u4ECE\u9879\u76EE\u4EE3\u5E01\u7684\u94F8\u9020\u3001\u91CA\u653E\u3001\u62CD\u5356\uFF0C\u5230\u4EA4\u6613</p><h3 id="shoyu-sushiswap-nft-marketplace" tabindex="-1">Sh\u014Dyu - SushiSwap NFT Marketplace <a class="header-anchor" href="#shoyu-sushiswap-nft-marketplace" aria-hidden="true" target="_blank" rel="noopener">#</a></h3><h3 id="sushiswap-\u672A\u6765" tabindex="-1">Sushiswap \u672A\u6765 <a class="header-anchor" href="#sushiswap-\u672A\u6765" aria-hidden="true" target="_blank" rel="noopener">#</a></h3><p><img src="'+u+'" alt="Sushiswap\u672A\u6765"></p>',94),c=[m];function b(_,k,M,f,x,S){return i(),e("div",null,c)}var B=a(g,[["render",b]]);export{w as __pageData,B as default};
